import React, { useState, useEffect} from "react";
import "./body.scss"
import axios from "axios";
const APIKEY = "4d2868699f6ab95203d92eab296afa21";
const Body = () => {

    const [movie , setMovie ] = useState([]);
    const [userInput, setUserInput] = useState('');

    const handleOnChange = event => {
        const { name, value } = event.target;
        setUserInput({ ...userInput, [name]: value });
    };

    useEffect(()=>{
        const fetchData =  async function (){
            const movies = await axios.get(`https://api.themoviedb.org/3/search/movie?" +
                "query=${userInput}&api_key=${APIKEY}&language=" +
                "en-US&page=1&include_adult=false`)
                //.then(movie => setMovie);
                .then(function(res){
                   setMovie(res.data.results)
                    console.log(res.data.results);

                })}
        fetchData();

    },[]);
    console.log(movie)

    return (
        <div>
            <p>
                Search:&nbsp;<input type="text" name="title"/>
                <button></button>
            </p>
            <ul>
                {
                    movie.map((film)=>(
                    <div>
                        <h4>{film.title}</h4>
                        <img src={`https://image.tmdb.org/t/p/w500/${film.poster_path}`}/>
                        <p>{film.overview}</p>
                    </div>

                ))}
            </ul>
        </div>
    )

};
export default Body;

