import React, { useState, useEffect} from "react";
import "./body.scss"
import axios from "axios";
const Body = () => {
    const APIKEY = "4d2868699f6ab95203d92eab296afa21";

    const [movie, setMovie] = useState([]);
    const [userInput, setUserInput] = useState('');

    const handleOnChange = event => {
        setUserInput(event.target.value);
        console.log(userInput)
    };

    const callSearch = (e) => {
        e.preventDefault()
        axios.get(`https://api.themoviedb.org/3/search/movie?query=${userInput}&api_key=${APIKEY}&language=en-US&page=1&include_adult=false`)
            .then(function (res) {
                setMovie(res.data.results)
                console.log(movie);

            }).catch(err=>console.log(err.response.data))
        console.log(userInput)
    };

    console.log(movie)

    return (
        <div>
                Movie:&nbsp;<input type="search" onChange={handleOnChange}/>
            <button type="submit" onClick={callSearch}>Search</button>

            <ul>
                {
                    movie.map((film)=>(

                        <div>
                            <h4>{film.title}</h4>
                            <img src={`https://image.tmdb.org/t/p/w500/${film.poster_path}`}/>
                            <p>{film.overview}</p>
                        </div>
                        // <Movie title={film.title} image={film.poster} />

                ))}
            </ul>

        </div>
    )

};
export default Body;


